import React, { useState, useEffect, useCallback } from 'react'

import styles from '../styles/Home.module.css'
import NavBar from './components/Nav'
import { GymMember } from './lib/User'
import { useSelector, useDispatch } from 'react-redux'
import { logout, reset } from './features/auth/authSlice'
import { useNavigate } from 'react-router-dom'
import { AppDispatch } from './app/store'
import './App.css';

function App(props: any) {

  const dispatch: AppDispatch = useDispatch()
  const navigate = useNavigate()
  const isLoggedIn: boolean = props.isLoggedIn
  const setIsLoggedIn: Function = props.setIsLoggedIn

  const user = props.user


  const handleLogout = (e: any) => {
    setIsLoggedIn(false)
    dispatch(logout())
    dispatch(reset())
    navigate('/login')
  }


  useEffect(() => {

    if(isLoggedIn === false) {
      navigate('/login')
    } 
  }, [navigate, isLoggedIn, setIsLoggedIn])

  return (
    <div className={styles.container}>
      <head>
        <title>Iron Vault</title>
        <meta name="description" content="Generated by create next app" />
        <link rel="icon" href="/favicon.ico" />
      </head>
      <div>
         <NavBar isLoggedIn={isLoggedIn} handleLogout={handleLogout} />

      </div>
      <div className='welcome-message'>
        { isLoggedIn ? <h1>Hello, {user.name}!</h1> : <></> }
      </div>
    </div>
  )
}


export default App;
